styleUrls: ['./user-myorders.component.css']
})
export class UserMyordersComponent implements OnInit {
// Filter Variables
filterOrderId: string = '';
filterStatus: string = '';
filterShopName: string = '';
filterTailorId: string = '';

// Status Options for Filter
statusOptions: string[] = ['Pending', 'In Progress', 'Completed', 'Cancelled'];

// Orders Data (Mock Data)
orders: any[] = [
{
orderId: '12345',
tailorId: '101',
shopName: 'Tailor Shop A',
orderedOn: new Date('2023-10-01'),
      deliveredOn: new Date('2023-10-10'),
      status: 'Completed',
      dressCategory: 'Suits',
      measurements: { chest: 40, waist: 32, length: 42 },
      design: { neckType: 'Notch Lapel', sleeveType: 'Full Sleeve' },
      price: 2000
    },
    {
      orderId: '67890',
      tailorId: '102',
      shopName: 'Tailor Shop B',
      orderedOn: new Date('2023-10-05'),
      deliveredOn: null,
      status: 'In Progress',
      dressCategory: 'Blouse',
      measurements: { bust: 36, waist: 30, length: 38 },
      design: { sleeveType: 'Half Sleeve', neckline: 'Round Neck' },
      price: 1500
    }
  ];

  // Filtered Orders
  filteredOrders: any[] = this.orders;

  // Order Details Modal
  isOrderDetailsModalOpen: boolean = false;
  selectedOrder: any = null;

  // Dashboard Data
  totalOrders: number = 0;
  statusChart: any;

  ngOnInit(): void {
    this.totalOrders = this.orders.length;
    this.applyFilters();
    this.createStatusChart();
  }

  // Apply Filters
  applyFilters(): void {
    this.filteredOrders = this.orders.filter(order => {
      return (
        (!this.filterOrderId || order.orderId.includes(this.filterOrderId)) &&
        (!this.filterStatus || order.status === this.filterStatus) &&
        (!this.filterShopName || order.shopName.toLowerCase().includes(this.filterShopName.toLowerCase())) &&
        (!this.filterTailorId || order.tailorId.includes(this.filterTailorId))
      );
    });
  }

  // Clear Filters
  clearFilters(): void {
    this.filterOrderId = '';
    this.filterStatus = '';
    this.filterShopName = '';
    this.filterTailorId = '';
    this.applyFilters();
  }

  // View Order Details
  viewOrderDetails(order: any): void {
    this.selectedOrder = order;
    this.isOrderDetailsModalOpen = true;
  }

  // Close Order Details Modal
  closeOrderDetailsModal(): void {
    this.isOrderDetailsModalOpen = false;
  }

  // Create Status Chart
  createStatusChart(): void {
    const statusCounts = this.statusOptions.map(status => {
      return this.orders.filter(order => order.status === status).length;
    });

    const ctx = document.getElementById('statusChart') as HTMLCanvasElement;
    this.statusChart = new Chart(ctx, {
      type: 'pie', // You can also use 'bar' for a bar chart
      data: {
        labels: this.statusOptions,
        datasets: [
          {
            label: 'Orders by Status',
            data: statusCounts,
            backgroundColor: [
              '#FF6384', // Pending
              '#36A2EB', // In Progress
              '#4BC0C0', // Completed
              '#FFCE56'  // Cancelled
            ]
          }
        ]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false, // Disable aspect ratio to fit the container
      }
    });
  }
}
<div class="dashboard-container">
  <!-- Dashboard Header -->
  <div class="dashboard-header">
    <h1>My Orders Dashboard</h1>
  </div>

  <!-- Dashboard Cards -->
  <div class="dashboard-cards">
    <div class="card">
      <h3>Total Orders Placed</h3>
      <p>{{ totalOrders }}</p>
    </div>
  </div>

  <!-- Charts Section -->
  <div class="charts-section">
    <div class="chart-card">
      <h3>Orders by Status</h3>
      <div class="chart-container">
        <canvas id="statusChart"></canvas>
      </div>
    </div>
  </div>

  <!-- Filter Section -->
  <div class="filter-section">
    <h2>Filter Orders</h2>
    <div class="filters">
      <input type="text" [(ngModel)]="filterOrderId" placeholder="Order ID" />
      <select [(ngModel)]="filterStatus">
        <option value="">All Statuses</option>
        <option *ngFor="let status of statusOptions" [value]="status">{{ status }}</option>
      </select>
      <input type="text" [(ngModel)]="filterShopName" placeholder="Shop Name" />
      <input type="text" [(ngModel)]="filterTailorId" placeholder="Tailor ID" />
      <button (click)="applyFilters()">Apply Filters</button>
      <button (click)="clearFilters()">Clear Filters</button>
    </div>
  </div>

  <!-- Orders Table -->
  <div class="orders-table">
    <h2>My Orders</h2>
    <table>
      <thead>
      <tr>
        <th>Order ID</th>
        <th>Tailor ID</th>
        <th>Shop Name</th>
        <th>Ordered On</th>
        <th>Delivered On</th>
        <th>Status</th>
        <th>Actions</th>
      </tr>
      </thead>
      <tbody>
      <tr *ngFor="let order of filteredOrders">
        <td>{{ order.orderId }}</td>
        <td>{{ order.tailorId }}</td>
        <td>{{ order.shopName }}</td>
        <td>{{ order.orderedOn | date }}</td>
        <td>{{ order.deliveredOn | date }}</td>
        <td>{{ order.status }}</td>
        <td>
          <button (click)="viewOrderDetails(order)">View Details</button>
        </td>
      </tr>
      </tbody>
    </table>
  </div>
</div>

<!-- Order Details Popup -->
<div *ngIf="isOrderDetailsModalOpen" class="modal-overlay">
  <div class="modal">
    <button class="close-button" (click)="closeOrderDetailsModal()">&times;</button>
    <h2>Order Details</h2>
    <div *ngIf="selectedOrder">
      <p><strong>Order ID:</strong> {{ selectedOrder.orderId }}</p>
      <p><strong>Tailor ID:</strong> {{ selectedOrder.tailorId }}</p>
      <p><strong>Shop Name:</strong> {{ selectedOrder.shopName }}</p>
      <p><strong>Ordered On:</strong> {{ selectedOrder.orderedOn | date }}</p>
      <p><strong>Delivered On:</strong> {{ selectedOrder.deliveredOn | date }}</p>
      <p><strong>Status:</strong> {{ selectedOrder.status }}</p>

      <h3>Dress Details</h3>
      <p><strong>Category:</strong> {{ selectedOrder.dressCategory }}</p>
      <p><strong>Measurements:</strong> {{ selectedOrder.measurements | json }}</p>
      <p><strong>Design:</strong> {{ selectedOrder.design | json }}</p>
      <p><strong>Price:</strong> ₹{{ selectedOrder.price }}</p>
    </div>
  </div>
</div>
