// Method to initialize the Leaflet map
private initMap(): void {
  // Check if the map container exists
  const mapContainer = document.getElementById('shop-map');
  if (!mapContainer) {
    console.error('Map container not found');
    return;
  }

  // Use the shop's coordinates if available, otherwise use default coordinates
  const shopLat = this.selectedShopDetails?.location?.latitude || 28.6139; // Default: New Delhi
  const shopLng = this.selectedShopDetails?.location?.longitude || 77.2090;

  // Initialize the map
  this.map = L.map('shop-map').setView([shopLat, shopLng], 13);

  // Add a tile layer (e.g., OpenStreetMap)
  L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
    attribution: '© OpenStreetMap contributors'
  }).addTo(this.map);

  // Add a marker for the shop's location
  L.marker([shopLat, shopLng])
    .addTo(this.map)
    .bindPopup('Shop Location')
    .openPopup();
}

 generateBill(): void {
  if (!this.validateOrderData()) {
    return;
  }

  const userId = localStorage.getItem('id');
  if (!userId) {
    alert('Please log in to place an order');
    return;
  }

  // Create the billDetails object with initial data
  this.billDetails = {
    customerId: Number(userId),
    customerName: localStorage.getItem('name') || 'Customer', // Get customer name from localStorage
    tailorId: Number(this.selectedShop.tailorId), // Use tailorId from selected shop
    shopName: this.selectedShop.shopName,
    orderDate: new Date(),
    deliveryDate: new Date(new Date().setDate(new Date().getDate() + 7)),
    gender: this.genderForm.get('gender')?.value?.toUpperCase() || 'MALE',
    dress: this.selectedDress,
    measurements: this.measurementForm.value,
    design: this.designForm.value,
    price: Number(this.selectedShop.price) || 0
  };

  console.log('Generated bill details:', this.billDetails);
  this.sendMeasurementData();
}

private validateOrderData(orderData?: any): boolean {
  // Case 1: Validating initial form data before generating bill
  if (!orderData) {
    if (!this.selectedShop?.tailorId) {
      alert('Please select a valid shop');
      return false;
    }

    if (!this.selectedDress?.name) {
      alert('Please select a valid dress');
      return false;
    }

    if (!this.measurementForm.valid) {
      alert('Please fill in all measurement fields');
      console.log('Invalid measurements:', this.measurementForm.value);
      return false;
    }

    if (!this.designForm.valid) {
      alert('Please fill in all design options');
      console.log('Invalid design options:', this.designForm.value);
      return false;
    }

    if (!this.genderForm.valid) {
      alert('Please select a gender');
      return false;
    }

    return true;
  }

  // Case 2: Validating order data before sending to backend
  const requiredFields = ['customerId', 'measureId', 'tailorId', 'shopName', 'orderDate', 'deliveryDate', 'status'];
  
  for (const field of requiredFields) {
    if (!orderData[field]) {
      console.error(`Missing required field in order data: ${field}`);
      return false;
    }
  }
  
  // Additional validation for numeric fields
  if (!Number.isInteger(orderData.customerId) || 
      !Number.isInteger(orderData.measureId) || 
      !Number.isInteger(orderData.tailorId)) {
    console.error('Invalid numeric fields in order data');
    return false;
  }

  return true;
}

sendMeasurementData(): void {
  if (!this.billDetails) {
    console.error('Bill details not generated');
    return;
  }

  // Format the design data
  const designData = {
    willProvideCloth: this.designForm.get('willProvideCloth')?.value,
    clothType: this.designForm.get('clothType')?.value || '',
    clothColor: this.designForm.get('clothColor')?.value || '',
    neckTypes: this.designForm.get('neckTypes')?.value,
    sleeveTypes: this.designForm.get('sleeveTypes')?.value,
    cuts: this.designForm.get('cuts')?.value,
    otherOptions: this.designForm.get('otherOptions')?.value
  };

  // Format the measurements data
  const measurementsData = Object.keys(this.measurementForm.value).reduce((acc, key) => {
    acc[key] = this.measurementForm.get(key)?.value;
    return acc;
  }, {} as any);

  const measurementData = {
    userId: this.billDetails.customerId,
    tailorId: this.billDetails.tailorId,
    gender: this.genderForm.get('gender')?.value?.toUpperCase(),
    category: this.selectedDress.name,
    design: JSON.stringify(designData),
    measurements: JSON.stringify(measurementsData),
    price: this.selectedShop.price
  };

  console.log('Sending measurement data:', measurementData);

  this.measurementService.createMeasurement(measurementData).subscribe({
    next: (response) => {
      if (response && response.measurement_id) {
        console.log('Created measurement with ID:', response.measurement_id);
        this.sendOrderData(response.measurement_id);
      } else {
        console.error('Invalid response:', response);
        alert('Error creating measurement');
      }
    },
    error: (error) => {
      console.error('Error creating measurement:', error);
      alert('Failed to create measurement');
    }
  });
}

sendOrderData(measurementId: number): void {
  if (!this.billDetails) {
    console.error('Bill details not generated');
    return;
  }

  if (!measurementId) {
    console.error('No valid measurement ID provided');
    return;
  }

  // Format order data with all required fields
  const orderData: OrderData = {
    clothType: this.designForm.get('clothType')?.value || 'Not Specified',
    clothColor: this.designForm.get('clothColor')?.value || 'Not Specified',
    customerId: this.billDetails.customerId,
    orderDate: new Date().toISOString(),
    measureId: measurementId, // Use the measurement ID from the measurement creation
    deliveryDate: new Date(new Date().setDate(new Date().getDate() + 7)).toISOString(),
    shopName: this.billDetails.shopName,
    status: 'YET_TO_PICK_UP',
    tailorId: this.billDetails.tailorId, // Use tailorId from billDetails
    willProvideCloth: this.designForm.get('willProvideCloth')?.value || true
  };

  console.log('Sending order data:', orderData);

  this.orderService.createOrder(orderData).subscribe({
    next: (response) => {
      console.log('Order data sent successfully:', response);
      
      // Update billDetails with the order ID from response
      if (response && response.orderId) {
        this.billDetails.orderId = response.orderId;
        console.log('Updated bill details with order ID:', this.billDetails);
      }
      
      alert('Order placed successfully!');
      this.router.navigate(['/user-home']);
    },
    error: (error) => {
      console.error('Error sending order data:', error);
      alert('Failed to place order. Please try again.');
    }
  });
}
}

<div class="container">
  <!-- Gender Selection -->
  <div class="step">
    <div class="gender-icons">
      <div class="gender-icon" (click)="selectGender('male')">
        <img src="assets/icons/man.png" alt="Male Icon">
      </div>
      <div class="gender-icon" (click)="selectGender('female')">
        <img src="assets/icons/woman.png" alt="Female Icon">
      </div>
    </div>
  </div>



  <!-- Dress Category Selection -->
  <div class="step">
    <h2>Select Dress Category</h2>
    <div class="dress-grid">
      <div *ngFor="let dress of dressCategories" class="dress-card" (click)="onDressSelect(dress)">
        <img [src]="dress.image" alt="{{ dress.name }}">
        <p>{{ dress.name }}</p>
      </div>
    </div>
  </div>

  <!-- Dress Details Modal -->
  <div *ngIf="isDressDetailsModalOpen" class="modal-overlay">
    <div class="modal">
      <!-- Close Button -->
      <button class="close-button" (click)="closeDressDetailsModal()">&times;</button>

      <!-- Dress Image -->
      <div class="dress-image-container">
        <img [src]="selectedDress.image" alt="{{ selectedDress.name }}" class="dress-image">
      </div>

      <!-- Dress Details -->
      <div class="dress-details">
        <h2><strong>{{ selectedDress.name }}</strong></h2>
        <p><strong>Description:</strong> {{ selectedDress.description }}</p>
      </div>

      <!-- Action Buttons -->
      <div class="modal-actions">
        <button (click)="selectDress(selectedDress)">OK</button>
        <button (click)="closeDressDetailsModal()">Cancel</button>
      </div>
    </div>
  </div>

  <!-- Tailor Shop Selection -->
  <div class="step" id="tailor-shops">
    <h2>Select Tailor Shop</h2>
    <div *ngIf="selectedDress; else noCategorySelected">
      <div *ngIf="tailorShops.length > 0" class="shop-list">
        <div *ngFor="let shop of tailorShops" class="shop-card" (click)="onShopSelect(shop)">
          <p><strong>Shop Name:</strong> {{ shop.shopName }}</p>
          <p><strong>Price for {{ selectedDress.name }}:</strong> {{ shop.price }}</p>
          <button (click)="openShopDetailsModal(shop.tailorId); $event.stopPropagation()">View Shop</button>
        </div>
      </div>
      <div *ngIf="tailorShops.length === 0">
        <p>No shops available for this category.</p>
      </div>
    </div>
    <ng-template #noCategorySelected>
      <p>No category selected.</p>
    </ng-template>
  </div>

  <!-- Fixed Buttons at Bottom -->
  <div class="fixed-buttons">
    <button [disabled]="!selectedShop" (click)="openDesignModal()">Enter Design</button>
    <button [disabled]="!selectedShop" (click)="openMeasurementModal()">Enter Measurement</button>
    <button [disabled]="price <= 0" (click)="openBillModal()">Generate Bill</button>
  </div>

</div>


<!-- Design Options Modal -->
<div *ngIf="isDesignModalOpen" class="modal-overlay">
  <div class="modal">
    <h2>Select Design Options</h2>
    <form [formGroup]="designForm">
      <!-- Yes/No Radio Buttons for Cloth Material -->
      <div class="radio-group">
        <label class="radio-label">Will you provide the cloth material?</label>
        <div class="radio-options">
          <label class="radio-option">
            <input
              type="radio"
              formControlName="willProvideCloth"
              [value]="true"
              (change)="onClothMaterialChange(true)"
            />
            <span>Yes</span>
          </label>
          <label class="radio-option">
            <input
              type="radio"
              formControlName="willProvideCloth"
              [value]="false"
              (change)="onClothMaterialChange(false)"
            />
            <span>No</span>
          </label>
        </div>
      </div>

      <!-- Additional Fields for Cloth Type and Color (if No is selected) -->
      <div *ngIf="!designForm.value.willProvideCloth" class="cloth-fields">
        <label>Cloth Type:</label>
        <input type="text" formControlName="clothType" placeholder="Enter cloth type" />

        <label>Cloth Color:</label>
        <input type="text" formControlName="clothColor" placeholder="Enter cloth color" />
      </div>

      <!-- Other Design Options -->
      <div *ngFor="let option of objectKeys(designOptions)" class="design-option">
        <label>{{ option }}</label>
        <div *ngIf="isArray(designOptions[option])">
          <select [formControlName]="option">
            <option *ngFor="let value of designOptions[option]" [value]="value">{{ value }}</option>
          </select>
        </div>
        <div *ngIf="!isArray(designOptions[option])">
          <input type="text" [formControlName]="option" />
        </div>
      </div>

      <!-- Modal Buttons -->
      <div class="modal-actions">
        <button type="button" (click)="closeDesignModal()">Close</button>
        <button type="button" (click)="saveDesignOptions()">Save</button>
      </div>
    </form>
  </div>
</div>


<!-- Measurement Modal -->
<div *ngIf="isMeasurementModalOpen" class="modal-overlay">
  <div class="modal">
    <h2>Enter Measurements</h2>
    <form [formGroup]="measurementForm">
      <div *ngFor="let field of measurementFields">
        <label>{{ field.label }}</label>
        <input [formControlName]="field.name" [placeholder]="field.placeholder">
      </div>
      <button type="button" (click)="closeMeasurementModal()">Close</button>
      <button type="button" (click)="calculatePrice()">Calculate Price</button>
    </form>
  </div>
</div>

<!-- Bill Modal -->
<div *ngIf="isBillModalOpen" class="modal-overlay">
  <div class="modal">
    <!-- Bill Heading -->
    <div class="bill-heading-box">
      <h2 class="modal-title">Bill Details</h2>
    </div>

    <div *ngIf="billDetails" class="bill-content">
      <!-- Table for Bill Details -->
      <table class="bill-table">
        <!-- Customer Details -->
        <thead>
        <tr class="section-header">
          <th colspan="2">Customer Details</th>
        </tr>
        </thead>
        <tbody>
        <tr>
          <td><strong>Name:</strong></td>
          <td>{{ billDetails.customerName }}</td>
        </tr>
        <tr>
          <td><strong>Customer ID:</strong></td>
          <td>{{ billDetails.customerId }}</td>
        </tr>
        <tr>
          <td><strong>Gender:</strong></td>
          <td>{{ billDetails.gender }}</td>
        </tr>
        </tbody>

        <!-- Order Details -->
        <thead>
        <tr class="section-header">
          <th colspan="2">Order Details</th>
        </tr>
        </thead>
        <tbody>
        <tr>
          <td><strong>Order ID:</strong></td>
          <td>{{ billDetails.orderId }}</td>
        </tr>
        <tr>
          <td><strong>Measurement ID:</strong></td>
          <td>{{ billDetails.measureId }}</td>
        </tr>
        <tr>
          <td><strong>Order Date:</strong></td>
          <td>{{ billDetails.orderDate | date }}</td>
        </tr>
        <tr>
          <td><strong>Delivery Date:</strong></td>
          <td>{{ billDetails.deliveryDate | date }}</td>
        </tr>
        <tr>
          <td><strong>Dress:</strong></td>
          <td>{{ billDetails.dress.name }}</td>
        </tr>
        </tbody>

        <!-- Shop Details -->
        <thead>
        <tr class="section-header">
          <th colspan="2">Shop Details</th>
        </tr>
        </thead>
        <tbody>
        <tr>
          <td><strong>Shop Name:</strong></td>
          <td>{{ billDetails.shopName }}</td>
        </tr>
        <tr>
          <td><strong>Shop ID:</strong></td>
          <td>{{ billDetails.shopId }}</td>
        </tr>
        </tbody>

        <!-- Price Details -->
        <thead>
        <tr class="section-header">
          <th colspan="2">Price Details</th>
        </tr>
        </thead>
        <tbody>
        <tr>
          <td><strong>Measurements:</strong></td>
          <td>
            <p>Chest: {{ billDetails.measurements.chest }}</p>
            <p>Waist: {{ billDetails.measurements.waist }}</p>
            <p>Shoulder: {{ billDetails.measurements.shoulder }}</p>
            <p>Length: {{ billDetails.measurements.length }}</p>
            <p>Sleeve: {{ billDetails.measurements.sleeve }}</p>
            <p>Sleeve: {{ billDetails.measurements.neck }}</p>
          </td>
        </tr>
        <tr>
          <td><strong>Design:</strong></td>
          <td>
            <p>Neck Type: {{ billDetails.design.neckTypes }}</p>
            <p>Sleeve Type: {{ billDetails.design.sleeveTypes }}</p>
            <p>Cuts: {{ billDetails.design.cuts }}</p>
            <p>Other Options: {{ billDetails.design.otherOptions }}</p>
          </td>
        </tr>
        <tr class="price-row">
          <td><strong>Total Price:</strong></td>
          <td>₹{{ billDetails.price }}</td>
        </tr>
        </tbody>
      </table>
    </div>

    <!-- Buttons -->
    <div class="modal-buttons">
      <button type="button" class="btn-close" (click)="closeBillModal()">Close</button>
      <button type="button" class="btn-download" (click)="downloadBill()">Download Bill</button>
    </div>
  </div>
</div>

