import { ViewEncapsulation } from '@angular/core';
import { Router } from '@angular/router';
import * as L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import { Component, OnInit } from '@angular/core';



@Component({
  selector: 'app-index',
  templateUrl: './index.component.html',
  styleUrls: ['./index.component.css'],
  encapsulation: ViewEncapsulation.None
})
export class IndexComponent implements OnInit {
isLoginPopupOpen = false;
isRoleSelectionPopupOpen = false;
isCustomerRegistrationPopupOpen = false;
isTailorRegistrationPopupOpen = false;

// For dynamic price inputs
showShirtsPrice = false;
showPantsPrice = false;
showKurtaPrice = false;
showPalazzaPantsPrice = false;
showSherwaniPrice = false;
showSuitsPrice = false;
showBlazersPrice = false;
showSalwaarKameezPrice = false;

map: any;
marker: any;


ngOnInit() {
    // Initialize the map when the component loads
    if (this.isTailorRegistrationPopupOpen) {
      this.initMap();
    }
  }

  initMap() {
    // Create a map instance
    this.map = L.map('map').setView([20.5937, 78.9629], 5); // Center on India

    // Add a tile layer (you can use OpenStreetMap or other providers)
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap contributors',
    }).addTo(this.map);

    // Add a marker for location selection
    this.marker = L.marker([20.5937, 78.9629], {
      draggable: true,
    }).addTo(this.map);

    // Update the location input when the marker is moved
    this.marker.on('dragend', () => {
      const latLng = this.marker.getLatLng();
      const locationInput = document.getElementById('location') as HTMLInputElement;
      locationInput.value = `Lat: ${latLng.lat.toFixed(4)}, Lng: ${latLng.lng.toFixed(4)}`;
    });

    // Allow clicking on the map to move the marker
    this.map.on('click', (e: L.LeafletMouseEvent) => {
      this.marker.setLatLng(e.latlng);
      const locationInput = document.getElementById('location') as HTMLInputElement;
      locationInput.value = `Lat: ${e.latlng.lat.toFixed(4)}, Lng: ${e.latlng.lng.toFixed(4)}`;
    });
  }

openLoginPopup() {
    this.isLoginPopupOpen = true;
  }

  closeLoginPopup() {
    this.isLoginPopupOpen = false;
  }

  openRoleSelectionPopup() {
    this.isRoleSelectionPopupOpen = true;
  }

  closeRoleSelectionPopup() {
    this.isRoleSelectionPopupOpen = false;
  }

  openCustomerRegistrationPopup() {
    this.isCustomerRegistrationPopupOpen = true;
    this.isRoleSelectionPopupOpen = false;
  }

  closeCustomerRegistrationPopup() {
    this.isCustomerRegistrationPopupOpen = false;
  }

  openTailorRegistrationPopup() {
    this.isTailorRegistrationPopupOpen = true;
    this.isRoleSelectionPopupOpen = false;
  }

  closeTailorRegistrationPopup() {
    this.isTailorRegistrationPopupOpen = false;
  }

  togglePriceInput(dressType: string) {
    switch (dressType) {
      case 'shirts':
        this.showShirtsPrice = !this.showShirtsPrice;
        break;
      case 'pants':
        this.showPantsPrice = !this.showPantsPrice;
        break;
      case 'kurta':
        this.showKurtaPrice = !this.showKurtaPrice;
        break;
      case 'palazzaPants':
        this.showPalazzaPantsPrice = !this.showPalazzaPantsPrice;
        break;
      case 'sherwani':
        this.showSherwaniPrice = !this.showSherwaniPrice;
        break;
      case 'suits':
        this.showSuitsPrice = !this.showSuitsPrice;
        break;
      case 'blazers':
        this.showBlazersPrice = !this.showBlazersPrice;
        break;
      case 'salwaarKameez':
        this.showSalwaarKameezPrice = !this.showSalwaarKameezPrice;
        break;
    }
  }
}



<header class="navbar">
  <div class="logo">
    <img src="assets/logo.png" alt="TailorPro Logo">
    <span>TailorPro</span>
  </div>
  <nav>
    <a href="#home">Home</a>
    <a href="#features">Features</a>
    <a href="#testimonials">Testimonials</a>
    <a (click)="openLoginPopup()">Login</a>
  </nav>
</header>

<!-- Login Popup -->
<div class="popup-overlay" *ngIf="isLoginPopupOpen">
  <div class="popup">
    <button class="close-btn" (click)="closeLoginPopup()">&times;</button>
    <h2>Login</h2>
    <form>
      <input type="email" placeholder="Email" required>
      <input type="password" placeholder="Password" required>
      <button type="submit">Login</button>
    </form>
  </div>
</div>

<!-- Register Role Selection Popup -->
<div class="popup-overlay" *ngIf="isRoleSelectionPopupOpen">
  <div class="popup">
    <button class="close-btn" (click)="closeRoleSelectionPopup()">&times;</button>
    <h2>Register As</h2>
    <button (click)="openCustomerRegistrationPopup()">Customer</button>
    <button (click)="openTailorRegistrationPopup()">Tailor</button>
  </div>
</div>

<!-- Customer Registration Popup -->
<div class="popup-overlay" *ngIf="isCustomerRegistrationPopupOpen">
  <div class="popup">
    <button class="close-btn" (click)="closeCustomerRegistrationPopup()">&times;</button>
    <h2>Customer Registration</h2>
    <form>
      <input type="text" placeholder="Name" required>
      <input type="email" placeholder="Email" required>
      <input type="tel" placeholder="Phone Number" required>
      <input type="password" placeholder="Password" required>
      <button type="submit">Register</button>
      <button type="button" (click)="closeCustomerRegistrationPopup()">Cancel</button>
    </form>
  </div>
</div>

<!-- Tailor Registration Popup -->
<div class="popup-overlay" *ngIf="isTailorRegistrationPopupOpen">
  <div class="popup">
    <button class="close-btn" (click)="closeTailorRegistrationPopup()">&times;</button>
    <h2>Tailor Registration</h2>
    <form>
      <input type="text" placeholder="Name" required>
      <input type="text" placeholder="Shop Name" required>
      <input type="tel" placeholder="Phone Number" required>
      <input type="email" placeholder="Email" required>
      <input type="password" placeholder="Password" required>
      <h3>Dresses You Stitch</h3>
      <label><input type="checkbox" (change)="togglePriceInput('shirts')"> Shirts</label>
      <input type="number" placeholder="Price for Shirts" *ngIf="showShirtsPrice">
      <label><input type="checkbox" (change)="togglePriceInput('pants')"> Pants</label>
      <input type="number" placeholder="Price for Pants" *ngIf="showPantsPrice">
      <label><input type="checkbox" (change)="togglePriceInput('kurta')"> Kurta</label>
      <input type="number" placeholder="Price for Kurta" *ngIf="showKurtaPrice">
      <label><input type="checkbox" (change)="togglePriceInput('palazzaPants')"> Palazza Pants</label>
      <input type="number" placeholder="Price for Palazza Pants" *ngIf="showPalazzaPantsPrice">
      <label><input type="checkbox" (change)="togglePriceInput('sherwani')"> Sherwani</label>
      <input type="number" placeholder="Price for Sherwani" *ngIf="showSherwaniPrice">
      <label><input type="checkbox" (change)="togglePriceInput('suits')"> Suits</label>
      <input type="number" placeholder="Price for Suits" *ngIf="showSuitsPrice">
      <label><input type="checkbox" (change)="togglePriceInput('blazers')"> Blazers</label>
      <input type="number" placeholder="Price for Blazers" *ngIf="showBlazersPrice">
      <label><input type="checkbox" (change)="togglePriceInput('salwaarKameez')"> Salwaar Kameez</label>
      <input type="number" placeholder="Price for Salwaar Kameez" *ngIf="showSalwaarKameezPrice">
      <!-- Map Container -->
      <div id="map" style="height: 300px; margin: 20px 0; border-radius: 8px;"></div>

      <!-- Location Input -->
      <input type="text" id="location" placeholder="Location" required readonly>
      <button type="submit">Register</button>
      <button type="button" (click)="closeTailorRegistrationPopup()">Cancel</button>
    </form>
  </div>
</div>

<section id="hero" class="hero-section">
  <div class="carousel">
    <div class="carousel-slide">
      <img src="assets/hero-image.png" alt="Tailor Hero Image 1">
      <img src="assets/hero-image.png" alt="Tailor Hero Image 2">
      <img src="assets/hero-image.png" alt="Tailor Hero Image 3">
    </div>
  </div>
  <div class="hero-content">
    <h1>Your Tailoring Business, Simplified</h1>
    <p>Manage orders, customers, and appointments seamlessly.</p>
    <button (click)="openRoleSelectionPopup()">Register Now!</button>
  </div>
</section>

<section id="features" class="features-section">
  <h2>Features</h2>
  <div class="feature">
    <i class="fas fa-cut"></i>
    <h3>Order Management</h3>
    <p>Track and manage tailoring orders with ease.</p>
  </div>
  <div class="feature">
    <i class="fas fa-user-friends"></i>
    <h3>Customer Profiles</h3>
    <p>Maintain detailed customer preferences.</p>
  </div>
  <div class="feature">
    <i class="fas fa-calendar-alt"></i>
    <h3>Appointment Scheduling</h3>
    <p>Schedule fittings and delivery dates easily.</p>
  </div>
</section>

<section id="testimonials" class="testimonials-section">
  <h2>What Our Users Say</h2>
  <div class="testimonial">
