import { Component, OnInit } from '@angular/core';
import { Chart } from 'chart.js';
import { OrderService } from '../../services/order.service'; // Import the OrderService
import { Order } from '../../models/order.model'; // Import the Order model

@Component({
  selector: 'app-tailor-dashboard',
  templateUrl: './tailor-dashboard.component.html',
  styleUrls: ['./tailor-dashboard.component.css'],
})
export class TailorDashboardComponent implements OnInit {
  // Dashboard statistics
  totalOrders = 0;
  completedOrders = 0;
  pendingOrders = 0;
  rejectedOrders = 0;
  inProgressOrders = 0;

  // Recent orders
  recentOrders: Order[] = [];

  // Chart data
  chartData = {
    labels: ['Completed', 'Pending', 'Rejected', 'In Progress'],
    datasets: [
      {
        data: [0, 0, 0, 0], // Initialize with zeros
        backgroundColor: ['#4CAF50', '#FFC107', '#F44336', '#2196F3'],
        hoverBackgroundColor: ['#45A049', '#FFB300', '#E53935', '#1E88E5'],
      },
    ],
  };

  // Chart options
  chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
  };

  constructor(private orderService: OrderService) {}

  ngOnInit(): void {
    const tailorId = Number(localStorage.getItem('id')); // Get tailorId from local storage
    if (tailorId) {
      this.fetchOrders(tailorId);
    }
  }

  // Fetch orders for the logged-in tailor
  fetchOrders(tailorId: number): void {
    this.orderService.getOrdersByTailorId(tailorId).subscribe(
      (orders: Order[]) => {
        this.recentOrders = orders.slice(0, 5); // Show the 5 most recent orders
        this.calculateStatistics(orders); // Calculate dashboard statistics
        this.updateChartData(); // Update the chart data
        this.createChart(); // Initialize the chart
      },
      (error) => {
        console.error('Failed to fetch orders:', error);
      }
    );
  }

  // Calculate dashboard statistics
  calculateStatistics(orders: Order[]): void {
    this.totalOrders = orders.length;
    this.completedOrders = orders.filter((order) => order.status === 'COMPLETED').length;
    this.pendingOrders = orders.filter((order) => order.status === 'PENDING').length;
    this.rejectedOrders = orders.filter((order) => order.status === 'REJECTED').length;
    this.inProgressOrders = orders.filter((order) => order.status === 'IN_PROGRESS').length;
  }

  // Update the chart data
  updateChartData(): void {
    this.chartData.datasets[0].data = [
      this.completedOrders,
      this.pendingOrders,
      this.rejectedOrders,
      this.inProgressOrders,
    ];
  }

  // Function to create the chart
  createChart(): void {
    const ctx = document.getElementById('ordersChart') as HTMLCanvasElement;
    new Chart(ctx, {
      type: 'doughnut',
      data: this.chartData,
      options: this.chartOptions,
    });
  }
}
<div class="dashboard-container">
  <h2>Tailor Dashboard</h2>

  <!-- Quick Stats -->
  <div class="quick-stats">
    <mat-card class="stat-card">
      <mat-card-title>Total Orders</mat-card-title>
      <mat-card-content>{{ totalOrders }}</mat-card-content>
    </mat-card>

    <mat-card class="stat-card">
      <mat-card-title>Completed Orders</mat-card-title>
      <mat-card-content>{{ completedOrders }}</mat-card-content>
    </mat-card>

    <mat-card class="stat-card">
      <mat-card-title>Pending Orders</mat-card-title>
      <mat-card-content>{{ pendingOrders }}</mat-card-content>
    </mat-card>

    <mat-card class="stat-card">
      <mat-card-title>Rejected Orders</mat-card-title>
      <mat-card-content>{{ rejectedOrders }}</mat-card-content>
    </mat-card>

    <mat-card class="stat-card">
      <mat-card-title>In Progress Orders</mat-card-title>
      <mat-card-content>{{ inProgressOrders }}</mat-card-content>
    </mat-card>
  </div>

  <!-- Orders Overview Chart -->
  <div class="chart-section">
    <mat-card>
      <mat-card-title>Orders Overview</mat-card-title>
      <mat-card-content>
        <canvas id="ordersChart"></canvas>
      </mat-card-content>
    </mat-card>
  </div>

  <!-- Recent Orders -->
  <div class="recent-orders">
    <mat-card>
      <mat-card-title>Recent Orders</mat-card-title>
      <mat-card-content>
        <table mat-table [dataSource]="recentOrders" class="mat-elevation-z8">
          <!-- Order ID Column -->
          <ng-container matColumnDef="orderId">
            <th mat-header-cell *matHeaderCellDef>Order ID</th>
            <td mat-cell *matCellDef="let order">{{ order.orderId }}</td>
          </ng-container>

          <!-- Customer ID Column -->
          <ng-container matColumnDef="customerId">
            <th mat-header-cell *matHeaderCellDef>Customer ID</th>
            <td mat-cell *matCellDef="let order">{{ order.customerId }}</td>
          </ng-container>

          <!-- Status Column -->
          <ng-container matColumnDef="status">
            <th mat-header-cell *matHeaderCellDef>Status</th>
            <td mat-cell *matCellDef="let order">
              <span [ngClass]="{
                'status-completed': order.status === 'COMPLETED',
                'status-pending': order.status === 'PENDING',
                'status-rejected': order.status === 'REJECTED',
                'status-in-progress': order.status === 'IN_PROGRESS'
              }">
                {{ order.status }}
              </span>
            </td>
          </ng-container>

          <!-- Delivery Date Column -->
          <ng-container matColumnDef="deliveryDate">
            <th mat-header-cell *matHeaderCellDef>Delivery Date</th>
            <td mat-cell *matCellDef="let order">{{ order.deliveryDate | date }}</td>
          </ng-container>

          <tr mat-header-row *matHeaderRowDef="['orderId', 'customerId', 'status', 'deliveryDate']"></tr>
          <tr mat-row *matRowDef="let row; columns: ['orderId', 'customerId', 'status', 'deliveryDate']"></tr>
        </table>
      </mat-card-content>
    </mat-card>
  </div>

  <!-- Quick Actions -->
  <div class="quick-actions">
    <button mat-raised-button color="primary">Create New Order</button>
    <button mat-raised-button color="accent">View All Orders</button>
  </div>
</div>
