: 5px;\r\n  cursor: pointer;\r\n  font-weight: bold;\r\n  transition: transform 0.2s ease, background 0.3s ease;\r\n}\r\n\r\n.hero-content button:hover {\r\n  transform: scale(1.05);\r\n  background: #333;\r\n  color: #FFD700;\r\n}\r\n\r\n/* Footer */\r\nfooter {\r\n  text-align: center;\r\n  padding: 20px;\r\n  background: #2A2F3F;\r\n  color: #FFF;\r\n}\r\n\r\n/* Features Section */\r\n.features-section {\r\n  padding: 50px 30px;\r\n  text-align: center;\r\n  background: #E0E0E0;\r\n}\r\n\r\n.features-section h2 {\r\n  font-size: 2.5rem;\r\n  margin-bottom: 30px;\r\n}\r\n\r\n.feature {\r\n  display: inline-block;\r\n  margin: 15px;\r\n  padding: 20px;\r\n  width: 300px;\r\n  border-radius: 8px;\r\n  background: #FFF;\r\n  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);\r\n  transition: transform 0.3s ease, box-shadow 0.3s ease;\r\n}\r\n\r\n.feature:hover {\r\n  transform: translateY(-10px);\r\n  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.15);\r\n}\r\n\r\n.feature i {\r\n  font-size: 2rem;\r\n  margin-bottom: 10px;\r\n  color: #4A90E2;\r\n}\r\n\r\n/* Testimonials Section */\r\n.testimonials-section {\r\n  padding: 40px 20px;\r\n  background: #FFF;\r\n}\r\n\r\n.testimonials-section h2 {\r\n  font-size: 2.2rem;\r\n  text-align: center;\r\n  margin-bottom: 20px;\r\n}\r\n\r\n.testimonial {\r\n  text-align: center;\r\n  padding: 15px;\r\n  border-left: 4px solid #F5A623;\r\n  margin-bottom: 15px;\r\n}\r\n\r\n.testimonial p {\r\n  font-size: 1rem;\r\n  color: #333;\r\n}\r\n\r\n.testimonial span {\r\n  display: block;\r\n  font-weight: bold;\r\n  margin-top: 10px;\r\n  color: #4A90E2;\r\n}\r\n\r\n/* Footer */\r\nfooter {\r\n  text-align: center;\r\n  padding: 20px;\r\n  background: #1C1C1C;\r\n  color: #FFF;\r\n}\r\n/* Popup Overlay */\r\n.popup-overlay {\r\n  position: fixed;\r\n  top: 0;\r\n  left: 0;\r\n  width: 100%;\r\n  height: 100%;\r\n  background: rgba(0, 0, 0, 0.5);\r\n  display: flex;\r\n  justify-content: center;\r\n  align-items: center;\r\n  z-index: 1000;\r\n  animation: fadeIn 0.3s ease;\r\n}\r\n\r\n@keyframes fadeIn {\r\n  from {\r\n    opacity: 0;\r\n  }\r\n  to {\r\n    opacity: 1;\r\n  }\r\n}\r\n\r\n/* Tailor Registration Popup Specific Styles */\r\n.popup {\r\n  background: white;\r\n  padding: 30px;\r\n  border-radius: 12px;\r\n  width: 400px;\r\n  max-height: 90vh; /* Limit height to 90% of viewport height */\r\n  overflow-y: auto; /* Enable vertical scrolling if content overflows */\r\n  box-shadow: 0 5px 20px rgba(0, 0, 0, 0.2);\r\n  animation: slideIn 0.3s ease;\r\n  box-sizing: border-box; /* Ensure padding is included in height calculation */\r\n}\r\n\r\n.popup h2 {\r\n  margin-bottom: 20px;\r\n  font-size: 24px;\r\n  font-weight: 600;\r\n  color: #333;\r\n}\r\n\r\n.popup label {\r\n  display: block;\r\n  margin: 10px 0 5px;\r\n  font-weight: 500;\r\n}\r\n\r\n.popup input,\r\n.popup select,\r\n.popup button {\r\n  width: 100%;\r\n  padding: 12px;\r\n  margin: 10px 0;\r\n  border: 1px solid #ddd;\r\n  border-radius: 8px;\r\n  font-size: 16px;\r\n  transition: border-color 0.3s ease;\r\n  box-sizing: border-box; /* Ensure padding is included in width calculation */\r\n}\r\n\r\n.popup input:focus,\r\n.popup select:focus {\r\n  border-color: #007bff;\r\n  outline: none;\r\n}\r\n\r\n.popup button {\r\n  background-color: #007bff;\r\n  color: white;\r\n  border: none;\r\n  cursor: pointer;\r\n  transition: background-color 0.3s ease;\r\n}\r\n\r\n.popup button:hover {\r\n  background-color: #0056b3;\r\n}\r\n\r\n.popup button[type=\"button\"] {\r\n  background-color: #6c757d;\r\n  margin-top: 10px;\r\n}\r\n\r\n.popup button[type=\"button\"]:hover {\r\n  background-color: #5a6268;\r\n}\r\n\r\n/* Checkbox and Price Input Styles */\r\n.popup input[type=\"checkbox\"] {\r\n  width: auto;\r\n  margin-right: 10px;\r\n}\r\n\r\n.popup input[type=\"number\"] {\r\n  margin-top: 5px;\r\n  width: calc(100% - 20px); /* Adjust width to fit container */\r\n}\r\n\r\n/* Ensure price inputs are displayed inline with checkboxes */\r\n.popup